<?phpif (!defined('_CAN_LOAD_FILES_'))	exit;class CelebrityBlockNav extends Module{	function __construct()	{		$this->name = 'celebrityblocknav';		$this->tab = 'front_office_features';		$this->version = 1.4;		parent::__construct();		$this->displayName = $this->l('Celebrity - Ivana Showroom Navigation');		$this->description = $this->l('Displays image blocks in Ivana showroom page.');	}	function install()	{		if (!parent::install())			return false;		if (!$this->registerHook('header')OR !$this->registerHook('celebrityhook'))			return false;		return true;	}	public function uninstall() {		return (parent::uninstall());	}		function hookcelebrityhook($params)	{		global $smarty,$cookie;				$smarty->assign( array(			 'this_path' => $this->_path		));                                /*Survey Vs Register*/                $showSite = isset($cookie->show_site) AND $cookie->show_site === 1;                if($cookie->logged)                {                    $customer = new Customer((int)($cookie->id_customer));                    if($customer->date_add >= '2012-09-17 00:00:00')                     {                        if (! $customer->hasCompletedSurvey() /*AND strtotime($customer->date_add) > $gadsSurveyStartDate*/ AND !$showSite)                         {                                $smarty->assign('no_butigim_link' , 1);                        }                      }                }                /*Survey Vs Register*/                		return $this->display(__FILE__, 'celebrityblocknav.tpl');	}	public function hookHeader($params)	{		if(strpos($_SERVER['PHP_SELF'], 'celebrity') !== false)			Tools::addCSS(($this->_path).'celebrityblocknav.css', 'all');	}}?>