<?phpif (!defined('_CAN_LOAD_FILES_'))	exit;class LookBookBlocknav extends Module{	function __construct()	{		$this->name = 'lookbookblocknav';		$this->tab = 'front_office_features';		$this->version = 1.4;		parent::__construct();		$this->displayName = $this->l('Lookbook Showroom Navigation');		$this->description = $this->l('Displays  image blocks in lookbook showroom page.');	}	function install()	{		if (!parent::install())			return false;		if (!$this->registerHook('header')OR !$this->registerHook('lookbookhook'))			return false;		return true;	}		public function uninstall() 	{		return (parent::uninstall());	}	function hooklookbookhook($params)	{		global $smarty,$cookie;		$category_details_hand = Category::searchByNameAndParentCategoryId((int)($cookie->id_lang), 'Handbags', 1);		$smarty->assign( array(			'this_path' => $this->_path,            'hand_link_rewrite' => $category_details_hand['link_rewrite']		));                /*Survey Vs Register*/                $showSite = isset($cookie->show_site) AND $cookie->show_site === 1;                if($cookie->logged)                {                    $customer = new Customer((int)($cookie->id_customer));                    if($customer->date_add >= '2012-09-17 00:00:00')                    {                        if (! $customer->hasCompletedSurvey() /*AND strtotime($customer->date_add) > $gadsSurveyStartDate*/ AND !$showSite)                        {                                $smarty->assign('no_butigim_link' , 1);                        }                    }                }                /*Survey Vs Register*/		return $this->display(__FILE__, 'lookbookblocknav.tpl');	}	public function hookHeader($params)	{		if(strpos($_SERVER['PHP_SELF'], 'lookbook') !== false)			Tools::addCSS(($this->_path).'lookbookblocknav.css', 'all');	}}?>