<?phpif (!defined('_CAN_LOAD_FILES_'))	exit;class productblocknav extends Module{	function __construct()	{		$this->name = 'productblocknav';		$this->tab = 'front_office_features';		$this->version = 1.4;		parent::__construct();		$this->displayName = $this->l('Product page logged out block');		$this->description = $this->l('Displays the image on product loggedout page.');	}	function install()	{		if (!parent::install())			return false;		if (!$this->registerHook('header')OR !$this->registerHook('productFooterBlock'))			return false;		return true;	}		public function uninstall() 	{		return (parent::uninstall());	}	function hookProductFooterBlock($params)	{		global $smarty,$cookie;				$smarty->assign( array(			 'this_path' => $this->_path,                         'HOOK_JOIN_NOW' =>  Module::hookExec('joinNow')		));		return $this->display(__FILE__, 'productblocknav.tpl');	}	public function hookHeader($params)	{		if(strpos($_SERVER['PHP_SELF'], 'product') !== false)		Tools::addCSS(($this->_path).'productblocknav.css', 'all');	}}?>