<?phpif (!defined('_CAN_LOAD_FILES_'))	exit;class showroomblocknav extends Module{	function __construct()	{		$this->name = 'showroomblocknav';		$this->tab = 'front_office_features';		$this->version = 1.4;		parent::__construct();		$this->displayName = $this->l('waiting showroom nav');		$this->description = $this->l('Displays the image on waiting showroom page.');	}	function install()	{		if (!parent::install())			return false;		if (!$this->registerHook('header')OR !$this->registerHook('beforeFooterBlock'))			return false;		return true;	}		public function uninstall() 	{		return (parent::uninstall());	}	function hookbeforeFooterBlock($params)	{		global $smarty,$cookie;		$category_details_hand = Category::searchByNameAndParentCategoryId((int)($cookie->id_lang), 'Handbags', 1);		$smarty->assign( array(			'this_path' => $this->_path,            'hand_link_rewrite' => $category_details_hand['link_rewrite']		));		return $this->display(__FILE__, 'showroomblocknav.tpl');	}	public function hookHeader($params)	{		if(strpos($_SERVER['PHP_SELF'], 'showroom')!== false || strpos($_SERVER['PHP_SELF'], 'waiting')!== false)			Tools::addCSS(($this->_path).'showroomblocknav.css', 'all');	}}?>